mapper.update:
UPDATE 
hobbyclass
SET total_steam = #{steamCount}   
WHERE class_no = #{hobbyClassNo}

mapper.delete:
DELETE 
FROM steam 
WHERE user_id = #{userId} 
      AND class_no = #{hobbyClassNo}

